const int N = 300 * 1000 + 17;
const int MOD = 1000 * 1000 * 1000 + 7;

int n;
int a[N], r[N];

bool read() {
    if (!(cin >> n))
        return false;

    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        r[--a[i]] = i;
    }

    return true;
}

void solve() {
    for (int i = n - 1; i >= 0; --i)
        if (a[i] != i) {
            int l = r[i];

            if (l % 2 != a[l] % 2) {
                cout << "No\n";
                return ;
            }

            for (int j = l; j < i; j += 2)
                if (a[j] > a[j + 1] && a[j + 1] > a[j + 2]) {
                    swap(a[j], a[j + 2]);
                } else {
                    cout << "No\n";
                    return ;
                }
        }

    cout << "Yes\n";
}

int32_t main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    while (read())
        solve();

    return 0;
}

