inline void merge_sort(int left,int right){
	if(left==right){
		return ;
	}
	int middle=(left+right)/2;
	merge_sort(left,middle);
	merge_sort(middle+1,right);
	int l=left,r=middle+1,p=left;
	while(l<=middle&&r<=right){
		switch(a[l]<=a[r]){
			case true:after[p]=a[l];l++;break;
			case false:after[p]=a[r];r++;ans+=middle-l+1;break;
		}
		p++;
	}
	while(l<=middle){
		after[p]=a[l];
		p++;l++;
	}
	while(r<=right){
		after[p]=a[r];
		p++;r++;
	}
	for(register int i=left;i<=right;i++){
		a[i]=after[i];
	}
}
