		for(int i=0;i<a.size();i++){
			if(a[i] == 1){
				pos = i;
				break;
			}
		}
		if(pos%2 != 0){
			puts("No"); return 0;
		}
		vector<int>b;
		if(pos == 0){
			int beg = a.size();
			for(int j=0;j<a.size();j++){
				if(a[j] == a[0]+j) continue;
