    while(c >= '0' && c <= '9') x = x * 10 + (c^48) , c = getchar();
    return f ? -x : x;
}

int main() {
    n = read();
    for(int i = 1 ; i <= n ; ++i) {
        num[i] = read();
        type[i] = (num[i] == i);
    }
    for(int i = 2 ; i < n ; ++i)
        if(type[i - 1] + type[i] + type[i + 1] == 0) return puts("No") & 0;

    for(int i = 1 , j; i <= n ; i = j + 1) {
        for(j = i ; j < n && type[j] != type[j + 1] ; ++j) {}
        tot = 0;
        for(int k = i ; k <= j ; ++k) {
            if(num[k] < i || num[k] > j) return puts("No") & 0;
            if(type[k] == 0) {
                val[++tot] = num[k];
                pre[tot] = suf[tot] = num[k];
            }
        }
        if(tot < 3) continue;
        for(int k = 2 ; k <= tot ; ++k) pre[k] = max(pre[k] , pre[k - 1]);
        for(int k = tot - 1 ; k ; --k) suf[k] = min(suf[k] , suf[k + 1]);
        for(int k = 2 ; k < tot ; ++k)
