	while(l<N){
		cout<<l<<endl;
		if (ite==piv.end()){
			ans=false;
			break;
		}
		if (*ite==l){
			l++;
			ite++;
			continue;
		}
		if (*ite>l+1){
			ans=false;
			break;
		}
		while(1){
			if (ite+1==piv.end()){
				break;
			}
			if (*(ite+1)==*ite+1){
				ans=false;
				goto BREAK;
			}
			if (*(ite+1)>*ite+2){
				break;
			}
			if (*ite+3>=N){
				break;
			}
			if (ite+2==piv.end() || *(ite+2)>*ite+3){
				ite++;
			}else{
				break;
			}
		}
		r=*ite+2;
