void quit(bool x){if(x)printf("No\n"),exit(0);}
void solve(int l,int r)
{
	while(l<=r&&p[l]==l)l++;
	while(l<=r&&p[r]==r)r--;
	if(l>r)return;
	v[0]=v[1]=0;
	for(int i=l;i<=r;i+=2)
	{
		quit(p[i]>r||p[i]<l||p[i]<v[p[i]>i]);
		v[p[i]>i]=p[i];
	}
}
int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)scanf("%d",&p[i]);
	int last=1;
	for(int i=1;i<=n;i++)
	{
		quit((p[i]-i)&1);
		if((p[i]==i)^(p[i+1]==i+1))continue;
		solve(last,i);
		last=i+1;
	}
	printf("Yes\n");
	return 0;
}

