int n;
int a[N];

bool work(int &n) {
	int p;
	for (int i = n; i; --i)
		if (a[i] == n) { p = i; break; }
	if (p == n) return --n, true;
	if ((n - p) & 1) return false;
	for (int i = p + 1; i <= n; i += 2)
		if (a[i] < a[i + 1]) return false;
	for (int i = p; i < n; ++i) a[i] = a[i + 1];
	for (int i = p; i < n; i += 2) swap(a[i], a[i + 1]);
	for (int i = p + 1; i < n; ++i) if (a[i] != i) return false;
	return n = p, true;
}

int main() {
	ios::sync_with_stdio(false);
	cin >> n;
	for (int i = 1; i <= n; ++i) cin >> a[i];
	while (n) if (!work(n)) return puts("No"), 0;
	puts("Yes");
	return 0;
}

