	if(r == N && p[r] = r) r--;
	if(l == r) return false;
	for(int i = l+1; i <= r; i += 2){
		if(p[i] != i) return false;
	}

	int pre = N+1;
	for(int i = r; i >= l; i -= 2){
		if(p[i] < i){
			if(pre < p[i]) return false;
			pre = i;
		}
	}
	return true;
}

