	for(int i = 1; i <= N; i++) cin >> p[i];

	int pre = 0;
	for(int i = 1; i <= N; i++){
		if(pre == 0){
			if(p[i] == i) continue;
			else pre = i;
		}
		else{
			if(p[i] == i && p[i-1] == i-1 && i >= 2){
				if(!check(pre, i-2)){
					cout << "No" << endl;
					return 0;
				}
				pre = 0;
			}
			if(p[i] != i && p[i-1] != i-1 && i >= 2){
				if(!check(pre, i-1)){
					cout << "No" << endl;
					return 0;
				}
				pre = i;
			}
		}
	}
	if(pre && !check(pre, N)) cout << "No" << endl;
	else cout << "Yes" << endl;
	return 0;
}

