			puts("No") ; return 0;
		}
		if(*odd.begin() == target){
			odd.erase(odd.begin());
			swap(odd,even);
			curmin++;
		}
		else{
			assert(even.size());
			int nxt = curmin+1;
			if(1){
				bool bad = 0;
				for(auto it=odd.begin(),it2 = even.begin();;it++,it2++){
					if((*it) == target){
						break;
					}
