    else {
      assert(0 <= l);
      require(l <= crr);
      if (i < crr) {
        set.insert(crr);
      }
      assert(i <= *set.begin());
      if (i == *set.begin()) {
        set.erase(set.begin());
        if (set.empty()) {
          cal(l, i);
        }
      }
    }
