        for(int i = la ; i <= ed ; i += 2) {
            v[++cnt] = p[i];
            id[p[i]] = cnt;
        }
        sort(v + 1,v + cnt + 1);
        for(int i = la ; i <= ed ; i += 2) {
            int t = lower_bound(v + 1,v + cnt + 1,p[i]) - v;
            if(t <= id[p[i]]) dir[i] = 0;
            else dir[i] = 1;
        }
        int L = 0,R = 0;
        for(int i = la ; i <= ed ; i += 2) {
            if(dir[i] == 0) {
                if(p[i] < L) {puts("No");return;}
                L = p[i];
            }
            else {
                if(p[i] < R) {puts("No");return;}
                R = p[i];
            }
        }
