	cin >> N;
	REP(i, N)cin >> p[i];
	REP(i, N)p[i]--;
}

void action(int l, int r) {
	int id = l + 1;
	while (id < r) {
		priority_queue<int> pq;
		if (p[id] == id && p[id - 1] > p[id] && p[id] > p[id + 1]) {
			if (max((pq.empty() ? -1 : pq.top()), p[id + 1]) == id - 1) {
				swap(p[id - 1], p[id + 1]);

				action(l, id - 1);
				action(id + 1, r);
				return;
			}
			pq.push(p[id - 1]);
		}
		id++;
	}
}


