int lowBit(int k) { return k & -k; }

void ch(int k) {
  for (; k <= n; k += lowBit(k))
    ++fw[k];
}

int qry(int k) {
  int ret = 0;
  for (; k; k &= k - 1)
    ret += fw[k];
  return ret;
}

void gg() {
  puts("No");
  exit(0);
}

