    if ((a[i] ^ i) & 1)
      gg();
  ll tot = 0, c0 = 0, c1 = 0;
  for (int i = n; i; --i) {
    tot += qry(i);
    ch(i);
  }
  memset(fw, 0, sizeof(fw));
  for (int i = n; i; --i)
    if (i & 1)
      ch(i);
    else
      c0 += qry(i);
  memset(fw, 0, sizeof(fw));
  for (int i = n; i; --i)
    if (i & 1)
      c1 += qry(i);
    else
      ch(i);
  puts((c0 + c1) * 3 == tot ? "Yes" : "No");
  return 0;
}

