bool action(int l, int r) {
	int id = l + 1;
	while (id < r) {
		priority_queue<int> pq;
		if (p[id] == id && p[id - 1] > p[id] && p[id] > p[id + 1]) {
			if (max((pq.empty() ? -1 : pq.top()), p[id + 1]) == id - 1) {
				swap(p[id - 1], p[id + 1]);

				bool bl = action(l, id - 1);
				bool br = action(id + 1, r);
				return (bl && br);
			}
			pq.push(p[id - 1]);
		}
		id++;
	}
	return true;
}


