int n, cum[MX];

bool OK(vi p) {
    /*for (int i: p) cout << i << " ";
    cout << "\n";*/

    if (sz(p) % 2 != 1) return 0;
    for (int& i: p) if (i < 0 || i >= sz(p)) return 0;
    if (sz(p) == 1) return 1;
    for (int i = 1; i < sz(p); i += 2) if (p[i] != i) return 0;
    vi le, ri;
    for (int i = 0; i < sz(p); i += 2) {
        if (p[i] < i) le.pb(p[i]);
        else if (p[i] == i) return 0;
        else ri.pb(p[i]);
    }
    F0R(i,sz(le)-1) if (le[i] > le[i+1]) return 0;
    F0R(i,sz(ri)-1) if (ri[i] > ri[i+1]) return 0;
    return 1;
}

bool ok(vi p) {
    for (int& i: p) i --;
    F0R(i,sz(p)) {
        int l = i, r = p[i]; if (l > r) swap(l,r);
        cum[l] ++; cum[r] --;
    }
    FOR(i,1,sz(p)) cum[i] += cum[i-1];
    F0R(i,sz(p)) {
        int I = i;
        while (cum[i]) i ++;
        vi tmp;
        FOR(j,I,i+1) tmp.pb(p[j]-I);
        if (!OK(tmp)) return 0;
    }
    return 1;
}

