#pragma warning (disable: 4996)

const int INF = 1000000000;

class RangeAddMinSegmentTree {
public:
	vector<pair<int, int>> dat;
	vector<int> lazy;
	int size_ = 1;

	void init(int sz) {
		while (size_ <= sz) size_ *= 2;
		dat.resize(size_ * 2, make_pair(0, INF));
		lazy.resize(size_ * 2, 0);

		for (int i = 0; i < size_; i++) {
			int pos = i + size_;
			dat[pos].second = i;
			while (pos >= 2) {
				pos >>= 1;
				dat[pos].second = min(dat[pos].second, i);
			}
		}
	}
	void refresh(int u) {
		lazy[u * 2 + 0] += lazy[u];
		lazy[u * 2 + 1] += lazy[u];
		pair<int, int> V1 = make_pair(dat[u * 2 + 0].first + lazy[u * 2 + 0], dat[u * 2 + 0].second);
		pair<int, int> V2 = make_pair(dat[u * 2 + 1].first + lazy[u * 2 + 1], dat[u * 2 + 1].second);
		dat[u] = min(V1, V2);
	}
	void add_(int l, int r, int x, int a, int b, int u) {
		if (l <= a && b <= r) { lazy[u] += x; return; }
		if (r <= a || b <= l) return;
		refresh(u);
		add_(l, r, x, a, (a + b) >> 1, u * 2);
		add_(l, r, x, (a + b) >> 1, b, u * 2 + 1);
		refresh(u);
	}
	void add(int l, int r, int x) {
		l = max(l, 0);
		add_(l, r, x, 0, size_, 1);
	}
	pair<int, int> query_(int l, int r, int a, int b, int u) {
		if (l <= a && b <= r) return make_pair(dat[u].first + lazy[u], dat[u].second);
		if (r <= a || b <= l) return make_pair(INF, -1);

		refresh(u);
		pair<int, int> V1 = query_(l, r, a, (a + b) >> 1, u * 2);
		pair<int, int> V2 = query_(l, r, (a + b) >> 1, b, u * 2 + 1);
		refresh(u);
		return min(V1, V2);
	}
	pair<int, int> query(int l, int r) {
		return query_(l, r, 0, size_, 1);
	}
	void delete_zero(int l, int r) {
		int cx = l;
		while (cx < r) {
			pair<int, int> F = query(cx, r);
			if (F.first != 0) return;
			add(F.second, F.second + 1, INF);
			cx = F.second + 1;
		}
	}
};

bool solve(vector<int> Z) {
	RangeAddMinSegmentTree X;
	X.init(Z.size() + 2);

	for (int i = 0; i < Z.size(); i++) {
		if (Z[i] >= i + 0) X.add(i - 1, i, 1);
		if (Z[i] >= i + 2) X.add(i + 1, Z[i], 1);
	}

	while (true) {
		pair<int, int> A = X.query(0, Z.size() - 1);
		if (A.first != 0) break;

		int pos1 = A.second, pos2 = A.second + 1;
		if (Z[pos1] >= pos1 + 0) X.add(pos1 - 1, pos1, -1);
		if (Z[pos1] >= pos1 + 2) X.add(pos1 + 1, Z[pos1], -1);
		if (Z[pos2] >= pos2 + 0) X.add(pos2 - 1, pos2, -1);
		if (Z[pos2] >= pos2 + 2) X.add(pos2 + 1, Z[pos2], -1);
		swap(Z[pos1], Z[pos2]);
		if (Z[pos1] >= pos1 + 0) X.add(pos1 - 1, pos1, 1);
		if (Z[pos1] >= pos1 + 2) X.add(pos1 + 1, Z[pos1], 1);
		if (Z[pos2] >= pos2 + 0) X.add(pos2 - 1, pos2, 1);
		if (Z[pos2] >= pos2 + 2) X.add(pos2 + 1, Z[pos2], 1);
	}

	for (int i = 0; i < Z.size(); i++) {
		if (Z[i] != i) return false;
	}
	return true;
}

