  for (int i = 1; i <= n; i++) {
    int x; std::cin >> x;
    a.at(i) = {x, i};
  }
  auto is_stable = [&] (auto p)         {return p.first == p.second;};
  auto rev       = [&] (auto p, auto q) {return p.first > q.first;};
  auto boundary  = [&] (auto p, auto q) {return is_stable(p) == is_stable(q);};
  auto check = [&] (int l, int r) {
    assert(1 <= l && l < r && r <= n + 1);
    if (r - l == 1) return is_stable(a.at(l));
    if (std::any_of(a.begin() + l, a.begin() + r, [l, r](auto p){
        return p.first < l|| r <= p.first;}))
      return false;
    std::vector<std::pair<int, int>> move, stable;
    for (int i = l; i < r; i++) {
      ((i - l) % 2 == 0 ? move : stable).push_back(a.at(i));
    }
    if (std::any_of(stable.begin(), stable.end(), [&](auto p){return !is_stable(p);}))
      return false;
    if (std::any_of(move.begin(), move.end(), is_stable)) return false;
    auto mid = std::adjacent_find(move.begin(), move.end(), rev);
    if (mid == move.end()) return false;
    assert(std::adjacent_find(move.begin(), mid, rev) == mid);
    return std::adjacent_find(mid + 1, move.end(), rev) == move.end();
  };
  auto err = [&] {
    std::cout << "No" << std::endl;
    exit(0);
  };
  int l = 1, r = -1;
  for (int i = 1; i <= n - 1; i++) {
    if (boundary(a.at(i), a.at(i + 1))) {
      r = i + 1;
      if (!check(l, r)) err();
      l = r;
    }
  }
  r = n + 1;
  if (!check(l, r)) err();
  std::cout << "Yes" << std::endl;
  return 0;
}

