inline int gi() {
  char ch=getchar(); int q=0,x=0;
  while(ch<'0'||ch>'9') q=(ch=='-'?1:q),ch=getchar();
  while(ch>='0'&&ch<='9') x=x*10+ch-'0',ch=getchar();
  return q?(-x):x;
}
int a[N],b[N];
int check(int l,int r){
  if (l>r) return 0;
  if (l==r) return a[l]!=l;
  int mn=inf,mx=0;
  for (int i=l; i<=r; ++i)
    mn=min(mn,a[i]),mx=max(mx,a[i]);
  if (mn!=l||mx!=r) return 1;
  for (int i=l+1; i<=r; i+=2)
    if (a[i]!=i) return 1;
  for (int i=l; i<=r; i+=2)
    if (a[i]==i) return 1;
  mn=0,mx=0;
  for (int i=l; i<=r; ++i){
    if (a[i]<i){
      if (a[i]<mn) return 1;
      mn=a[i];
    }
    if (a[i]>i){
      if (a[i]<mx) return 1;
      mx=a[i];
    }
  }
  return 0;
}
