struct BIT
{
	int n;
	int p[N] , tr[N];
	inline void Add(int pos , int val) { while(pos <= ::n) tr[pos] += val , pos += (pos & (-pos)); }
	inline int Ask(int pos) { int ans = 0; while(pos) ans += tr[pos] , pos -= (pos & (-pos)); return ans; }
	LL calc()
	{
		LL ans = 0;
		for(int i = 1 ; i <= n ; ++i)
			ans += Ask(::n) - Ask(p[i]) , Add(p[i] , 1);
		for(int i = 1 ; i <= n ; ++i) Add(p[i] , -1);
		return ans;
	}
}R;
