int n, a[N];
bool go() {
	bool done = true;
	for (int i = 1; i <= n; ++i)
		if (a[i] != i)
			done = false;
	if (done)
		return true;
	for (int i = 2; i < n; ++i) {
		if (a[i - 1] > a[i] && a[i] > a[i + 1]) {
			swap(a[i - 1], a[i + 1]);
			if (go()) {
				return true;
			}
			swap(a[i - 1], a[i + 1]);
		}
	}
	return false;
}
