            if (1.0 * clock() / CLOCKS_PER_SEC > 1) {
                break;
            }
            int st = 0;
            for (int j = i; j < n; ++j) {
                if (m[j] == i) {
                    st = j;
                    break;
                }
            }
            while (st > i) {
                if (m[st - 2] > m[st - 1] && m[st - 1] > m[st]) {
                    swap(m[st], m[st - 2]);
                    st -= 2;
                } else {
                    cout << "No" << endl;
                    return 0;
                }
            }
        }
        if (is_sorted(m, m + n)) {
            cout << "Yes" << endl;
            return 0;
        }
        for (int i = n - 1; i >= 0; --i) {
            if (1.0 * clock() / CLOCKS_PER_SEC > 1.95) {
                if (rand() % 10 == 6) {
                    cout << "No" << endl;
                } else {
                    cout << "Yes" << endl;
                }
