            int st = 0;
            for (int j = 0; j < n; ++j) {
                if (m[j] == i) {
                    st = j;
                    break;
                }
            }
            while (st < i) {
                if (m[st] > m[st + 1] && m[st + 1] > m[st + 2]) {
                    swap(m[st], m[st + 2]);
                    st += 2;
                } else {
                    cout << "No" << endl;
                    return 0;
                }
            }
        }
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }
    return 0;
}

