using namespace std;
const int MAX = 321123;
int N;
int a[MAX], p[MAX], q[MAX];

int main(){

    // 1. 入力情報取得.
    scanf("%d", &N);
    for(int i = 0; i < N; i++) scanf("%d", a + i), a[i]--;

    // 2. データの並び替え情報を保存.
    // 左側.
    p[0] = a[0];
    for(int i = 1; i < N; i++) p[i] = max(p[i - 1], a[i]);
    // 右側.
    q[N - 1] = a[N - 1];
    for(int i = N - 2; i >= 0; i--) q[i] = min(q[i + 1], a[i]);

    // 3. p[i] = i と出来るかのチェック.
