    for(int i = 1; i < N - 1; i++) if(a[i] != i && p[i - 1] > a[i] && q[i + 1] < a[i]) ok = false;
    for(int i = 0; i < N - 1; i++) if((a[i] == i) == (a[i + 1] == i + 1) && p[i] != i) ok = false;

    // 4. 出力 ～ 後処理.
    if(ok) printf("%s\n", "Yes");
    else   printf("%s\n", "No");
    return 0;

}

