const int INF = 0x3f3f3f3f;

int n, p[300005];

struct segt
{
	int mind[1048576], maxd[1048576];

	void init()
	{
		memset(mind, INF, sizeof(mind));
		memset(maxd, 0, sizeof(maxd));
	}

	void modify(int pos, int dat)
	{
		pos += 524288;
		mind[pos] = maxd[pos] = dat;
		while(pos > 1) {
			pos >>= 1;
			mind[pos] = min(mind[pos << 1], mind[pos << 1 | 1]);
			maxd[pos] = max(maxd[pos << 1], maxd[pos << 1 | 1]);
		}
	}

	int qmind(int l, int r)
	{
		l += 524288; r += 524288;
		int ret = INF;
		while(l < r) {
			if(l & 1) ret = min(ret, mind[l]);
			if(!(r & 1)) ret = min(ret, mind[r]);
			l = l + 1 >> 1;
			r = r - 1 >> 1;
		}
		if(l == r) ret = min(ret, mind[l]);
		return ret;
	}

	int qmaxd(int l, int r)
	{
		l += 524288; r += 524288;
		int ret = 0;
		while(l < r) {
			if(l & 1) ret = max(ret, maxd[l]);
			if(!(r & 1)) ret = max(ret, maxd[r]);
			l = l + 1 >> 1;
			r = r - 1 >> 1;
		}
		if(l == r) ret = max(ret, maxd[l]);
		return ret;
	}
}tre1, tre2;

int occ[300005];
int lmax[300005], rmin[300005];

int main()
{
	scanf("%d", &n);
	rep1(i, n) scanf("%d", &p[i]);
	rep1(i, n) if(p[i] - i & 1) {
		printf("No\n");
		return 0;
	}
	tre1.init();
	tre2.init();
	rep1(i, n) if(i & 1) tre1.modify(i, p[i]);
	else tre2.modify(i, p[i]);
	rep1(i, n) occ[p[i]] = i;
	rep1(i, n) {
		lmax[i] = p[i];
		if(i > 2) lmax[i] = max(lmax[i], lmax[i - 2]);
	}
	for(int i = n; i >= 1; i --) {
		rmin[i] = p[i];
		if(i < n - 1) rmin[i] = min(rmin[i], rmin[i + 2]);
	}
	bool ok = true;
	rep1(i, n) if(lmax[i] > p[i] && rmin[i] < p[i]) {
		printf("No\n");
		return 0;
	}
	rep1(i, n) if(i & 1) {
		if(occ[i] < i) ok &= tre2.qmaxd(occ[i], i) <= i;
		else ok &= tre2.qmind(i, occ[i]) >= i;
	} else {
		if(occ[i] < i) ok &= tre1.qmaxd(occ[i], i) <= i;
		else ok &= tre1.qmind(i, occ[i]) >= i;
	}
	printf(ok ? "Yes\n" : "No\n");
	return 0;
}

