ll inq(ll x, ll y, ll mod)
{
    if (!y) re 1 % mod;
    ll l = inq(x, y / 2, mod);
    if (y % 2) re l * l % mod * x % mod;
    re l * l % mod;
}

ll inv(ll x, ll mod)
{
