	{
		if(q[i - 1] != i - 1 && q[i] != i && q[i + 1] != i + 1) {puts("No"); return 0;}
		a[i] = (q[i] == i);
	}
	for(int i = 1, j; i <= n; i = j + 1)
	{
		j = i;
