const int maxn = 3e5;
int n, a[maxn + 3], m[2], b[2][maxn + 3], c[maxn + 3];

void add(int x, int y) {
	for (int i = x; i <= maxn; i += i & -i) c[i] += y;
}

int sum(int x) {
	int y = 0;
	for (int i = x; i; i ^= i & -i) y += c[i];
	return y;
}

int solve(int a[], int n) {
	int res = 0;
	for (int i = n; i; i--) res += sum(a[i]), add(a[i], 1);
	memset(c, 0, sizeof(c));
	return res;
}

