		if ((a[i] ^ i) & 1) puts("No"), exit(0);
		b[i & 1][++m[i & 1]] = a[i];
	}
	puts((solve(b[0], m[0]) + solve(b[1], m[1])) * 3 - solve(a, n) ? "No" : "Yes");
	return 0;	
}

