int solve(int l,int r){
//	cout<<l<<" "<<r<<endl;
	for (int i=l; i<=r; ++i) if (a[i]<l||a[i]>r) return puts("No"),0;
	int prev=-INF;
	for (int i=l; i<=r; ++i)
	if (a[i]>i){
		if (a[i]>prev) prev=a[i];
		else return puts("No"),0;
	}
	prev=-INF;
	for (int i=l; i<=r; ++i)
	if (a[i]<i){
		if (a[i]>prev) prev=a[i];
		else return puts("No"),0;
	}
