#include <bits/stdc++.h>
using namespace std;
void check(vector<int> &v, int st, int fin){
    vector<int> good;
    set<int> unused;
    for (int i=st; i <= fin; i++) unused.insert(v[i]);
    int ind = fin;
    while (ind >= st){
        set<int>::iterator it = unused.end();
        it--;
        int num = *it;
        while (true){
            unused.erase(unused.find(v[ind]));
            if (v[ind] == num){
                good.push_back(ind);
                ind--;
                break;
            }
            ind--;
        }
    }
    reverse(good.begin(), good.end());
    int u = 0;
    for (int i=st; i <= fin; i++){
        if (u < good.size() && good[u] == i){
            u++;
            continue;
        }
        int pl = i;
        int diff = i - st;
        if (diff%2 == 0) pl -= 2*u;
        pl++;
        if (pl == v[i]) continue;
        cout << "No";
        exit(0);
    }
}
void solve(vector<int> &v, int st){
    if (st == v.size()) return;
    set<int> ms;
    for (int i=st; i < v.size(); i++){
        ms.insert(v[i]-st);
        set<int>::iterator it = ms.end();
        it--;
        int mx = *(it);
        if (mx == ms.size()){
            check(v, st, i);
            solve(v, i+1);
            return;
        }
    }
}
signed main() {
    int n;
    cin >> n;
    vector<int> v(n);
    for (int i=0; i < n; i++) cin >> v[i];
    solve(v, 0);
    cout << "Yes";

}

