bool check(int l, int r)
{
	if(r == N && p[r] == r) r--;
	//cout << l << " " << r << endl;
	if(l == r) return false;
	for(int i = l+1; i <= r; i += 2){
		if(p[i] != i) return false;
	}

	vector<int> mov, rem;
	for(int i = l; i <= r; i += 2){
		if(p[i] < i) mov.push_back(p[i]);
		else rem.push_back(p[i]);
	}
	for(int i = 0; i < (int)mov.size()-1; i++){
		if(mov[i] > mov[i+1]) return false;
	}
	for(int i = 0; i < (int)rem.size()-1; i++){
		if(rem[i] > rem[i+1]) return false;
	}
	return true;
}

