			cout << "No" << endl;
			return 0;
		}
	}

	int pre = 0;
	for(int i = 1; i <= N; i++){
		if(pre == 0){
			if(p[i] == i) continue;
			else pre = i;
		}
		else{
			if(p[i] == i && p[i-1] == i-1 && i >= 2){
				if(!check(pre, i-2)){
