ll cnt;
int chk2;
int solve(int pv) {
    if (pv == 0) return 1;
    int pos;
    rep(i, pv) {
        if (p[i] == pv) {
            pos = i;
            break;
        }
    }
    for (int i = pos; i + 2 < pv; i += 2) {
        if (!(p[i] > p[i + 1] && p[i + 1] > p[i + 2])) return 0;
        swap(p[i], p[i + 2]);
    }
    repp(i, pos, pv) {
        if (p[i] != i + 1) return 0;
    }
    rep(i, pos) if (p[i] >= pos + 1) return 0;
    if (!solve(pos)) return 0;
    return 1;
}
void upd(int p, int val) {
    for (t[p += n] = val; p > 1; p >>= 1) t[p >> 1] = t[p] + t[p ^ 1];
}
ll qry(int l, int r) {
    ll ret = 0;
    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) ret += t[l++];
        if (r & 1) ret += t[--r];
    }
    return ret;
}
int main() {
