int N;
std::vector<int> S;
int f[MAXN];

struct BIT{
    #define lowbit(x) ((x)&(-(x)))
    int tree[MAXN];

    inline void add(int pos,int d){
        while(pos < MAXN){
            (tree[pos] += d) %= ha;
            pos += lowbit(pos);
        }
    }

    inline int query(int pos){
        int res = 0;
        while(pos){
            (res += tree[pos]) %= ha;
            pos -= lowbit(pos);
        }
        return res;
    }
}bit;

inline bool cmp(P x,P y){
    return x.fi == y.fi ? x.se > y.se : x.fi < y.fi;// 同列不得转移
}

int main(){
