int n;

struct Binary_Indexed_Tree {

	int c[N];

	void init() {
		memset(c, 0, sizeof c);
	}

	int lowbit(int x) {
		return x & (-x);
	}

	void add(int x) {
		while (x <= n) {
			c[x]++;
			x += lowbit(x);
		}
	}

	int sum(int x) {
		int ret = 0;
		while (x) {
			ret += c[x];
			x -= lowbit(x);
		}
		return ret;
	}

}T;

LL Inverse(int *A, int m) {
	T.init();
	LL ret = 0;
	Forr(i, m, 1) ret += T.sum(A[i]), T.add(A[i]);
	return ret;
}

