			return 0;
		}
	}

	For(i, 1, m) P[i] = A[i];
	LL s = Inverse(P, m);
	For(i, 1, m / 2) P[i] = A[i * 2]; 
	LL a = Inverse(P, m / 2);
	For(i, 1, (m + 1) / 2) P[i] = A[i * 2 - 1];
	LL b = Inverse(P, (m + 1) / 2);

	if (s == (a + b) * 3) puts("Yes");
	else puts("No");

