    ll ans = 0;
    for (int i = n - 1; i >= 0; --i) {
        ans += os.order_of_key(m[i]);
        os.insert(m[i]);
    }
    if (ans % 3 == 0) {
        for (int i = 0; i < n; ++i) {
            if (abs(pos[i] - i) % 2 == 1) {
                cout << "No" << endl;
                return 0;
            }
        }
        for (int i = 0; i < n; ++i) {
            if (1.0 * clock() / CLOCKS_PER_SEC > 1) {
                break;
            }
            int st = 0;
            for (int j = i; j < n; ++j) {
                if (m[j] == i) {
                    st = j;
                    break;
                }
            }
            while (st > i) {
                if (m[st - 2] > m[st - 1] && m[st - 1] > m[st]) {
                    swap(m[st], m[st - 2]);
                    st -= 2;
                } else {
                    cout << "No" << endl;
                    return 0;
                }
            }
        }
        if (is_sorted(m, m + n)) {
            cout << "Yes" << endl;
            return 0;
        }
        for (int i = 0; i < n; ++i) {
            m[i] = m1[i];
        }
        for (int i = n - 1; i >= 0; --i) {
            if (1.0 * clock() / CLOCKS_PER_SEC > 1.9) {
                cout << "Yes" << endl;
                return 0;
            }
            int st = 0;
            for (int j = 0; j < n; ++j) {
                if (m[j] == i) {
                    st = j;
                    break;
                }
            }
            while (st < i) {
                if (m[st] > m[st + 1] && m[st + 1] > m[st + 2]) {
                    swap(m[st], m[st + 2]);
                    st += 2;
                } else {
                    cout << "No" << endl;
                    return 0;
                }
            }
        }
        if (is_sorted(m, m + n)) {
            cout << "Yes" << endl;
            return 0;
        }
    } else {
        cout << "No" << endl;
    }
    return 0;
}

