  for(ll i=0;i<N;){
    if(p[i]==i){
      i++;
      continue;
    }
    ll j=i;
    bool same=false;
    vector<int> tot;
    for(j=i;j<N&&(p[j]==j)==same;j++){
      tot.push_back(p[j]);
