    for(ll k=i;k<j;k+=2){
      if(p[k]==k){
        ok=false; break;
      }
      if(p[k]>k){
        if(preinc>p[k]){
          ok=false; break;
        }
        preinc=p[k];
      }else if(p[k]<k){
        if(predec>p[k]){
          ok=false; break;
        }
        predec=p[k];
      }
    }
    if(!ok)break;
    i=j;
  }

  if(ok)cout<<"Yes"<<endl;
  else cout<<"No"<<endl;

  return 0;
}

