const int N = 333333;
int n,a[N];
int solve(int l,int r){
    if(l==r)
        return 0;
    int i,x=0,y=0;
    for(i=l;i<=r;i=i+2){
        if(a[i]<i){
            if(x>a[i])
                return 0;
            x=a[i];
        }
        else{
            if(y>a[i])
                return 0;
            y=a[i];
        }
    }
    return 1;
}
int main(){
    int i,l,r,x=1;
    scanf("%d",&n);
    for(i=1;i<=n;i=i+1)
        scanf("%d",a+i);
    l=-2,r=-2;
    for(i=1;i<=n;i=i+1){
        if(a[i]!=i){
            if(i-r!=2){
                if(r>0)
                   x&=solve(l,r);
                l=i;
            }
            r=i;
        }
    }
    if(r>0)
        x&=solve(l,r);
    if(x)
        cout<<"Yes";
    else
        cout<<"No";
    return 0;
}

