	v[N + 1] = N + 1;
	v[N + 2] = N + 2;
	for (int i = 1; i <= N; i++) {
		cin >> v[i];
	}
	L = -1;
	vector<pair<int, int>>p;
	for (int i = 0; i <= N + 2; i++){
		if (L == -1) {
			if (v[i] != i) {
				L = i;
			}
		}
		else {
			if (v[i] == i) {
				if (i - L == 2) {
					cout << "No\n";
					return 0;
				}
				if ((i - L) % 2 == 0) {
					p.emplace_back(L, R);
					L = -1;
				}
			}
			if (v[i] != i) {
				if ((i - L) % 2) {
					p.emplace_back(L, R);
					L = i;
				}
				R = i;
			}
		}
	}
	//for (auto i : p) {
	//	cout << i.first << " " << i.second << endl;
	//}
	for (auto i : p) {
		L = i.first;
		R = i.second;
		vector<int>w;
		for (int j = L; j <= R; j += 2) {
			w.push_back(v[j]);
		}
		auto u = w;
		sort(u.begin(), u.end());
		for (int j = L; j <= R; j += 2) {
			w.push_back(v[j]);
		}
		vector<int>sum(w.size(), 1);
		bool flag = false;
		for (int i = 1; i < w.size(); i++) {
			if (w[i] > w[i - 1]) {
				sum[i] = sum[i - 1] + 1;
				if (sum[i] == u.size()) {
					flag = true;
				}
			}
		}
		if (!flag) {
			cout << "No\n";
			return 0;
		}
		for (int j = R; j >= L; j -= 2) {
			v[j] = u.back();
			u.pop_back();
		}
	}
	for (int i = 1; i <= N; i++) {
		if (v[i] != i) {
			cout << "No\n";
			return 0;
		}
	}
	cout << "Yes\n";
	return 0;
}

