const int N_MAX = 300007;
int n, p[N_MAX];
struct FenwickTree {
  int sum[N_MAX];
  void modify(int x) {
    while (x <= n)
      ++sum[x], x += x & -x;
  }
  int query(int x) {
    int ret = 0;
    while (x)
      ret += sum[x], x -= x & -x;
    return ret;
  }
}bit[3];

int main() {

  scanf("%d", &n);
  for (int i = 1; i <= n; ++i)
    scanf("%d", &p[i]);

  bool flag = 1;
  long long I[3] = {0, 0, 0};
  for (int i = n; i; --i) {
    flag &= (i & 1) == (p[i] & 1);
    I[i & 1] += bit[i & 1].query(p[i]);
    I[2] += bit[2].query(p[i]);
    bit[i & 1].modify(p[i]);
    bit[2].modify(p[i]);
  }
  printf(flag && (I[0] + I[1]) * 3 == I[2] ? "Yes\n" : "No\n");
  return 0;
}

