    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    auto check = [&](int l, int r){
        if ((r-l)&1) N();
        for (int i = l+1; i < r; i+=2) {
            if (a[i] != i) N();
        }
        for (int i = l; i <= r; i+=2) {
            if (a[i] == i) N();
        }
        int x = 0, y = 0;
        for (int i = l; i <= r; i+=2) {
            if (a[i] < i) {
                if (a[i] < x) N();
                x = a[i];
            } else {
                if (a[i] < y) N();
                y = a[i];
            }
        }
    };
    int l = 1, r;
