bool rev(int p) {
	if (vis[p + 1]) return false;
	if (a[p] > a[p + 1] && a[p + 1] > a[p + 2]) {
		swap(a[p + 2], a[p]);
		vis[p] = vis[p + 2] = true;
		return true;
	} else return false;
}

