        forn(j, v.size()) {
           if(o != v[j])
                ok &= 0;
            o += 2;
        }
        if(!ok) nope();
    }
    cout << "Yes\n";
#ifdef LOCAL
    cerr << "Time elapsed: " << 1.0 * (clock() - tStart)/ CLOCKS_PER_SEC << " s.\n";
#endif
    return 0;
}

