 bool fun(int a, int b)
{
	if ((b - a) % 2) return false;
	for (int i = a + 1; i < b; i += 2) {
		if (p[i] != i) return false;
	}
	while (!tor.empty()) tol.pop();
	while (!tol.empty()) tol.pop();
	for (int i = a; i <= b; i += 2) {
		if (p[i] > i) tor.push(p[i]);
		else tol.push(p[i]);
	}
	while (!tor.empty()) {
		tol.push(tor.front());
		tor.pop();
	}
	for (int i = a; i <= b; i += 2) {
		p[i] = tol.front();
		tol.pop();
	}
