struct Edge {
    int s, t, w;
};

int main() {
    int L;
    cin >> L;
    int r = log2(L);
    int n = r + 1;
    vector<Edge> g;

    rep(i, r) {
        Edge e1, e2;
        e1.w = 0;
        e1.s = i+1;
        e1.t = i+2;
        e2.w = pow(2, i);
        e2.s = i+1;
        e2.t = i+2;
        g.push_back(e1);
        g.push_back(e2);
    }

    int t = n-1;
    bool update = true;
    while (update) {
        update = false;
        if (L - pow(2, t-1) >= pow(2, r)) {
            Edge e;
            e.w = L - pow(2, t-1);
            e.s = t;
            e.t = n;
            g.push_back(e);
            update = true;
            L -= pow(2, t-1);
            t--;
        }
    }
    cout << n << ' ' << g.size() << endl;
    for (auto v : g) {
        cout << v.s << ' ' << v.t << ' ' << v.w << endl;
    }
}

